module.exports = function(gulp){
	require('./server.js')(gulp);
	require('./app.js')(gulp);
	
	gulp.task('start',['server','build-app']);
	gulp.task('default',function(){
		var argv = process.argv.slice(2).join(' ');
		require('gulp-nodemon')({
			'exec': argv ? 'gulp start ' + argv : 'gulp start',
			'watch':['mocker']
		})
	});
}